export class Mainchara {
    constructor(name, gender, job){
        this.name = name
        this.gender = gender
        this.job = job
    }
    introduce(){
        return`I am ${this.name}.My job is ${this.job}`
    }
    batou(){
        if (this.gender === 'male') {
            return 'Bakayaro'
        }
        return 'Anta baka!?'
    }
}

export class Nagisa extends Mainchara {
    constructor(name, gender, job){
        super(name, gender, job)

    }
    loop(time){
        return` I am ${time} kaime no jinsei`
    }
}
